mapper.select:
SELECT calendarno, labeldate, label, title, content, cnt, regdate, seqno
FROM calendar
WHERE calendarno = #{calendarno}

mapper.update:
UPDATE calendar
SET cnt = cnt + 1
WHERE calendarno = #{calendarno}

UPDATE calendar
SET labeldate = #{labeldate}, label = #{label}, title = #{title}, content = #{content}, seqno = #{seqno}
WHERE calendarno = #{calendarno}
