mapper.select:
SELECT id, title, artist, genre, mood, era, region, rdate, visible, seqno
FROM category
WHERE id = #{id}

mapper.update:
UPDATE category
SET title=#{title}, artist=#{artist}, genre=#{genre}, mood=#{mood}, era=#{era}, region=#{region}, seqno=#{seqno}, visible=#{visible}, rdate=SYSDATE
WHERE id=#{id}

UPDATE category
SET seqno=seqno-1
WHERE id=#{id}

UPDATE category
SET seqno=seqno+1
WHERE id=#{id}

UPDATE category
SET visible='N'
WHERE id=#{id}

UPDATE category
SET visible='Y'
WHERE id=#{id}
